def GetLSProData(page_data,Cookie_Jar,m):
  import re, requests, urllib
  headers = {'User-Agent': 'User-Agent=Mozilla/5.0 (iPhone; U; CPU iPhone OS 4_2_1 like Mac OS X; en-us)', 'Referer': 'http://mitv.radiotormentamx.com'}

  url=re.findall('',page_data)[0]
  if 'fid' in url:
	return 'doregex[fid]'
  else:
	return 'doregex[youtube]'
  strurl = re.findall('''source:\s*['"]([^'"]+)''', page_data)[0]
  strurl += '|{}'.format(urllib.urlencode(headers))
  return strurl

